mapper.update:
update t_position
set positionName = #{positionname,jdbcType=VARCHAR},
  positionType = #{positiontype,jdbcType=INTEGER},
  remark = #{remark,jdbcType=VARCHAR},
  orderId=#{orderId}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, positionName, positionType, remark,orderId
from t_position
where id = #{id,jdbcType=INTEGER}
